if (headers != null) {
    for (java.util.Map.Entry<java.lang.String, java.util.Collection<java.lang.String>> entry : headers.entrySet()) {
        java.lang.String headerName = entry.getKey();
        if (!result.containsKey(headerName)) {
            result.put(headerName.toLowerCase(java.util.Locale.ROOT), new java.util.LinkedList<java.lang.String>());
        }
        result.get(headerName).addAll(entry.getValue());
    }
}
