if (model != null) {
    final hivemall.model.WeightValue probe = new hivemall.model.WeightValue();
    final java.lang.Object[] forwardMapObj = new java.lang.Object[2];
    final org.apache.hadoop.io.FloatWritable fv = new org.apache.hadoop.io.FloatWritable();
    final hivemall.utils.collections.IMapIterator<java.lang.Object, hivemall.model.IWeightValue> itor = model.entries();
    while (itor.next() != (-1)) {
        itor.getValue(probe);
        if (!probe.isTouched()) {
            continue;// skip outputting untouched weights

        }
        final float v = probe.get();
        if (v == 0.0F) {
            continue;
        }
        fv.set(v);
        java.lang.Object k = itor.getKey();
        forwardMapObj[0] = k;
        forwardMapObj[1] = fv;
        forward(forwardMapObj);
        numForwarded++;
    } 
}
