if (node != null) {
    if (node.getChildren().size() > 0) {
        for (org.apache.iotdb.db.metadata.mnode.MNode child : node.getChildren().values()) {
            if (!java.util.regex.Pattern.matches(nodeReg.replace("*", ".*"), child.getName())) {
                continue;
            }
            if (idx == length) {
                res.add(parent + node.getName());
            } else {
                findChildNodePathInNextLevel(child, nodes, idx + 1, (parent + node.getName()) + org.apache.iotdb.db.conf.IoTDBConstant.PATH_SEPARATOR, res, length);
            }
        }
    } else if (idx == length) {
        java.lang.String nodeName = node.getName();
        res.add(parent + nodeName);
    }
}
